import React from 'react'
import ReactDOM from 'react-dom'
import localImage from './images/01.jpg'
const welcome = 'Welcome to 30 days of React'
const title = 'Getting Started with React'
const subtitle = 'JavaScript Library'
const author = {
  firstName: 'Don',
  lastName: 'Cjjj'
}
const headerStyles = {
  backgroundColor: '#61DBFB',
  fontFamily: 'Helvetica Neue',
  padding: 25,
  lineHeight: 1.5,
}

const header = (
  <div className='header-wrapper'>
    <img src={localImage} width={200} height={200} />
    <h1>{welcome}</h1>
    <h2>{title}</h2>
    <h3>{subtitle}</h3>
    <p>Instructor: {author.firstName} {author.lastName}</p>
  </div>
)

const numOne = 3
const numTwo = 2
const result = (
  <p>
    {numOne} + {numTwo} = {numOne + numTwo}
  </p>
)

const yearBorn = 2020
const currentYear = 2024
const age = currentYear - yearBorn
const personAge = (<p>{age}</p>)

const tech = ['html', 'css', 'javaScript']
const techFormatted = tech.map((tech)=><li key={tech}>{tech}</li>)

const main  = (
  <main>
      <div className='main-wrapper'>
            {personAge}
          <ul>{techFormatted}</ul>
      </div>
  </main>
)

const footer = (
  <footer style={ {
    backgroundColor: '#61DBFB',
  }}>
      <p>Copyright@2023</p>
  </footer>
)

const app = (
  <>

    {header}
    {main}
    {footer}
  </>
)
const rootElement = document.getElementById('root')

ReactDOM.render(app, rootElement)
